<?php
/**
 * Administration Menu
 *
 * @package FutureShop\WordPress
 * @author Future Shop <support@futureshop.cloud>
 * @since 0.0.1
 */

namespace FutureShop\WordPress\Admin\Menus;

/**
 * Menu class
 */
final class Menu {
	/**
	 * Main menu position.
	 *
	 * @var number $position Admin menu position in WordPress.
	 */
	const POSITION = 55;

	/**
	 * Main menu slug.
	 *
	 * @var string $slug Main admin menu slug.
	 */
	const SLUG = 'future-shop';

	/**
	 * WordPress administration capability.
	 *
	 * @var string $capability Administration capability.
	 */
	const CAPABILITY = 'manage_options';

	/**
	 * Init function for the Menu class.
	 *
	 * @return void
	 */
	public function init() {
		\add_action( 'admin_menu', [ __CLASS__, 'add_menu_page' ] );
		\add_action( 'admin_menu', [ __CLASS__, 'remove_submenu_page' ], 100 );
	}

	/**
	 * Render the admin menu page.
	 *
	 * @return void
	 */
	public static function add_menu_page() {
		\add_menu_page(
			__( 'Future Shop', 'future-shop' ),
			__( 'Future Shop', 'future-shop' ),
			self::CAPABILITY,
			self::SLUG,
			null,
			self::svg_icon(),
			self::POSITION
		);

		// Add a menu seperator between the last menu item and the FutureShop menu item.
		if ( current_user_can( 'manage_options' ) ) {
			$seperator = self::POSITION - 1;

			$GLOBALS['menu'][ $seperator ] = [ // phpcs:ignore WordPress.WP.GlobalVariablesOverride.Prohibited
				'',
				'read',
				'separator' . $seperator,
				'',
				'wp-menu-separator',
			];

			ksort( $GLOBALS['menu'] );
		}
	}

	/**
	 * Remove the submenu page for the top-level menu.
	 *
	 * @return void
	 */
	public static function remove_submenu_page() {
		remove_submenu_page( self::SLUG, self::SLUG );
	}

	/**
	 * Render the admin menu page SVG icon.
	 *
	 * @return string SVG icon markup.
	 */
	public static function svg_icon() {
		return 'data:image/svg+xml;base64,';
	}
}
